// Initialize the Typeahead/Bloodhound engine
// (See Typeahead/Bloodhound documentation for more info)
var roadEngine = new Bloodhound({
  datumTokenizer: Bloodhound.tokenizers.obj.whitespace("tekst"),
  queryTokenizer: Bloodhound.tokenizers.whitespace,
  remote: {
    url: "https://dawa.aws.dk/vejnavne/autocomplete?q=%QUERY",
    wildcard: "%QUERY",
    cache: true
  }
});
roadEngine.clearRemoteCache();
roadEngine.initialize();

// Typeahead/Bloodhound dataset
var roadEngineDataset = {
  name: "road",
  display: "tekst",
  source: roadEngine.ttAdapter()
};
// Typeahead/Bloodhound config options
var roadEngineConfig = {
  autoselect: true,
  highlight: true,
  hint: true,
  minLength: 3
};

var attachTypeaheadHandler = function(ed) {
  if (ed) {
    // Remove any existing Typeahead/Bloodhound handlers
    $(ed.input).typeahead('destroy');
    // Attach the Typeahead/Bloodhound engine to the JSON-Editor input field
    $(ed.input).typeahead(roadEngineConfig, roadEngineDataset)
    .on("typeahead:asyncrequest", function() {
      // Enable spinner
      $(ed.input).parent().addClass("input-loading");
    })
    .on("typeahead:asynccancel typeahead:asyncreceive", function() {
      // Disable spinner
      $(ed.input).parent().removeClass("input-loading");
    });

    // Change parent (wrapper) display from inline-block to block
    $(ed.input).parent().css("display", "block");
  }
};

// The following lines are mandatory and readonly. You can add custom code above and below.
if (jseditor instanceof window.JSONEditor) jseditor.destroy();
jseditor = new window.JSONEditor(document.querySelector("#json-editor-form"), jedata);

// When JSON-Editor is ready perform action on field(s)
jseditor.on('ready', function() {
  // Get the JSON-Editor for the  static "roadname" field
  var ed = this.getEditor('root.roadname');
  // Attach Typeahead handler to static field
  attachTypeaheadHandler(ed);

});

jseditor.on('change', function() {

  // Loop through all editors/fields
  for (var key in this.editors) {
    if (this.editors.hasOwnProperty(key)) {
      var ed = this.getEditor(key);
      // We need the parent to be an objerct and the parents parent to be an array
      if (ed.parent && ed.parent.parent && ed.parent.schema.type == 'object' && ed.parent.parent.schema.type == 'array') {
        // If the editor key is "name" then we attach the typeahead handler
        // You could also add an additional check for the name of the
        // "parent.parent.key" if you have more array objects
        if (ed.key == 'name') {
          // Attach Typeahead handler to dynamic field
          attachTypeaheadHandler(ed);
        }
      }
    }
  }

});

style:
.tt-query {
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
}
.tt-hint {
  color: #999;
}
.tt-menu {
  width: 100%;
  min-width: 200px;
  padding: 4px 0;
  background-color: #fff;
  border: 1px solid rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
}
.tt-suggestion {
  padding: 3px 20px;
  line-height: 24px;
}
.tt-suggestion.tt-cursor,
.tt-suggestion:hover {
  color: #fff;
  background-color: #0097cf;
}
.tt-suggestion p {
  margin: 0;
}
.input-loading {}
.input-loading:after {
  position: absolute;
  top: 8px;
  right: 8px;
  content: "\e030";
  font-family: "Glyphicons Halflings";
  color: #0097cf;
  font-size: 20px;
  font-weight: 400;
  line-height: 1;
  transform: rotate(0deg);
  animation: spinner 1s linear infinite;
}
@keyframes spinner {
  100% {
    transform: rotate(359deg);
  }
}
